        <div v-for="(category, idx) in categories" :key="idx" class="category" :id="category">
            <div class="category__title">{{category.charAt(0).toUpperCase() + category.slice(1)}}</div>
            <div class="product__selections">
                <!-- @click="selectProduct(idx, category)" -->
                <div class="selection" v-for="n in 3" >
                    <img src="/Header/macroons.jpg" alt="" class="selection-photo" />
                    <input class="radiobutton" v-model="selections[idx]" type="radio" :name="category" :value="`${category}${n}`" />
                </div>
            </div>

            <div v-if="selections[idx]" class="products">
                <div class="product__card" v-for="item in shop[category][selections[idx]]">
                    <img :src="item.photoUrl" alt="" class="product-photo">
                    <div class="product-name">{{ item.name }}</div>
                    <hr class="product-hr">
                    <div class="product__amout-section">
                        <div @click="subractNum(item)" class="product-quantity">-</div>
                        <div class="product-amount">{{ item.amount }}</div>
                        <div @click="addNum(item)" class="product-quantity">+</div>
                    </div>
                </div>
            </div>
        </div>


        <div class="category" id="cakes" style="margin-top: 100px;">
            <div class="category__title">Cakes</div>



