                <!-- v-if="cake[cakeCategoties[idx--]] || key === `size`" -->
                <div  class="product__selections" v-if="cake.size">
                    <div class="selection" v-for="(n, i) in item.length">
                        <img src="/Header/macroons.jpg" alt="" class="selection-photo" />
                        <input class="radiobutton" v-model="cake[key]" type="radio" :name="`cake-${key}`" :value="cakeCreation[key][i]" />
                    </div>
                </div>
                <hr class="cake-hr"/>
            </div>
        </div>

        <Footer />
    </div>
</template>

<script>
	export default {
		name: "Shop",
        data() {
            return {
                selections: ["", "", ""],
                categories: ["macaroons", "cupcakes"],
                purchase: [],
                shop: {
                    macaroons:{
                        macaroons1:{
