                                id: 5,
                                name: "macaroons3",
                                photoUrl: "data:image/jpeg;base64,",
                                amount: 0,
                            }
                        },
                    },
                    cupcakes:{
                        cupcakes1:{
                            cupcake1:{
                                id: 6,
                                name: "cupcakes1",
                                photoUrl: "data:image/jpeg;base64,",
                                amount: 0,
                            },
                            cupcake2:{
                                id: 7,
                                name: "cupcakes1",
                                photoUrl: "data:image/jpeg;base64,",
                                amount: 0,
                            },
                            cupcake3:{
                                id: 8,
