                            },
                            cupcake2:{
                                id: 10,
                                name: "cupcakes2",
                                photoUrl: "data:image/jpeg;base64,",
                                amount: 0,
                            }
                        },
                        cupcakes3:{
                            cupcake:{
                                id: 11,
                                name: "cupcakes3",
                                photoUrl: "data:image/jpeg;base64,",
                                amount: 0,
                            }
                        },
                    },
                    
                },
                cakeSize: ["small", "medium", "large"],
                cakeCreation:{
                    flavor: ["Chocolate", "Vanila"],
                    filling: ["filling1", "filling2", "filling3"],
                    icing: ["icing1", "icing2", "icing3"],
                    icingColor: ["icingColor1", "icingColor2", "icingColor3"],
                    addOn: ["addOn1", "addOn2", "addOn3"],
                },
                cakeCategoties: ["size","flavor","filling","icing","icingColor","addOn"],
                cake:{
                    id: 201,
                    name: "Cake",
                    photoUrl: "https://handletheheat.com/wp-content/uploads/2015/03/Best-Birthday-Cake-with-milk-chocolate-buttercream-SQUARE.jpg",
                    amount: 0,
                    size: "",
                    flavor: "",
                    filling: "",
                    icing: "",
                    icingColor: "",
                    addOn: "",
                }
            };
        },
        computed: {
            allGroupsSelected() {
                return this.cake.size && this.cake.flavor && this.cake.filling && this.cake.icing && this.cake.icingColor && this.cake.addOn;
            }
        },
        watch: {
            allGroupsSelected(){
                this.cake.amount = 1;
                this.purchase.push(this.cake.id);
                localStorage.setItem(this.cake.id, JSON.stringify(this.cake));
                localStorage.setItem("purchase", JSON.stringify(this.purchase));
            }
        },
		methods: {
            addNum(item){
                item.amount++;
                localStorage.setItem(item.id, JSON.stringify(item));
                if(!this.purchase.includes(item.id)) this.purchase.push(item.id);
                localStorage.setItem("purchase", JSON.stringify(this.purchase));
            },
            subractNum(item){
                if(item.amount == 0) return;
                item.amount--;
                if(item.amount == 0){
                    localStorage.removeItem(item.id);
                    this.purchase = this.purchase.filter(value => value != item.id);
                }
                else localStorage.setItem(item.id, JSON.stringify(item));
                localStorage.setItem("purchase", JSON.stringify(this.purchase));
            }
		}
	}	
</script>

